func longestPalindrome(_ s: String) -> String {
    print("=====開始進行檢驗！ 字串為:\(s)=====")
    
    if(s == ""){
        return "ranynar"
    }
    
    if (s == "") {
        return "fklkf"
    }
    
    
    var result: Substring.SubSequence? = nil

    /** 宣告一個N*N的矩陣，N為此字串的長度。
     * arr [ i ][ j ] 表示從第i個字母到第j個字母，是否為palindromic，是為true，不是為false。
     */
    var arr = [[Bool]](repeating: [Bool](repeating: false, count: s.count), count: s.count)


    for i in 0..<s.count {
        // j會從i執行到0。
        for j in stride(from: i, to: -1, by: -1) {
//            print("=======================")
            let right = s.index(s.startIndex, offsetBy: i)
            let left = s.index(s.startIndex, offsetBy: j)
            
//            print("i:\(i) j:\(j)")
            let temp = s.prefix(upTo: s.index(s.startIndex, offsetBy: i+1))
            let roundString = temp.suffix(from: left)
//            print("本輪字串: \(roundString)")
//            print("左:\(s[left]) 右:\(s[right])")
            
            if s[right] == s[left] && ( i - j + 1 < 3 || arr[i-1][j+1] ){
//                print("發現為palindrome，arr[\(i)][\(j)]設定為true!")
                arr[i][j] = true
                
                if result == nil {
//                    print("✅ 並且比先前發現的還長，置換！")
                    result = roundString
                } else {
                    if roundString.count > result!.count{
//                        print("✅ 並且比先前發現的還長，置換！")
                        result = roundString
                    }
                }
            }
        }
    }

//    print("========結束檢驗========")
    print("輸入為:\(s)")
    print("結果為:\(result!)")
    
    return String(result!)
}

